def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    fire_q = deque()
    agent_q = deque([(0, 0)])
    fire_grid = [[0] * n for _ in range(n)]
    agent_grid = [[0] * n for _ in range(n)]
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_q.append((i, j))
                fire_grid[i][j] = 1
            elif grid[i][j] == '#':
                fire_grid[i][j] = agent_grid[i][j] = -1

    def is_valid(x, y):
        return 0 <= x < n and 0 <= y < n and (grid[x][y] != '#')

    def spread_fire():
        size = len(fire_q)
        for _ in range(size):
            (x, y) = fire_q.popleft()
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if is_valid(nx, ny) and fire_grid[nx][ny] == 0:
                    fire_grid[nx][ny] = fire_grid[x][y] + 1
                    fire_q.append((nx, ny))

    def move_agent():
        size = len(agent_q)
        for _ in range(size):
            (x, y) = agent_q.popleft()
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if is_valid(nx, ny) and agent_grid[nx][ny] == 0 and (fire_grid[nx][ny] == 0):
                    agent_grid[nx][ny] = agent_grid[x][y] + 1
                    if (nx, ny) == (n - 1, n - 1):
                        return True
                    agent_q.append((nx, ny))
        return False
    for t in range(1, n * n + 1):
        spread_fire()
        if move_agent():
            return t
    return -1

