from collections import Counter
def best_split(feature, target):
    """Determine the numerical threshold that produces the highest information gain."""
    if len(set(feature)) == 1:
        return (None, 0.0)
    parent_entropy = entropy(target)
    data = list(zip(feature, target))
    data.sort()
    best_threshold = None
    best_information_gain = 0.0
    for i in range(1, len(data)):
        if data[i][0] == data[i - 1][0]:
            continue
        threshold = (data[i][0] + data[i - 1][0]) / 2
        left_data = [x[1] for x in data if x[0] < threshold]
        right_data = [x[1] for x in data if x[0] >= threshold]
        left_entropy = entropy(left_data)
        right_entropy = entropy(right_data)
        information_gain = parent_entropy - len(left_data) / len(data) * left_entropy - len(right_data) / len(data) * right_entropy
        if information_gain > best_information_gain:
            best_threshold = threshold
            best_information_gain = information_gain
    if best_threshold is None:
        return (None, 0.0)
    return (round(best_threshold, 4), round(best_information_gain, 4))
def entropy(labels):
    """Calculate the entropy of a set of labels."""
    counts = Counter(labels)
    entropy = 0.0
    for count in counts.values():
        p = count / len(labels)
        entropy -= p * math.log2(p)
    return entropy

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer.

    The function must return a tuple (pooled_output, grad_input), both rounded
    to the nearest 4-th decimal and converted to regular Python lists using
    `tolist()`.
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    out_h = (H - p_h) // s_h + 1
    out_w = (W - p_w) // s_w + 1
    pooled_output = np.zeros((N, C, out_h, out_w))
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for h in range(out_h):
                for w in range(out_w):
                    window = X[n, c, h * s_h:h * s_h + p_h, w * s_w:w * s_w + p_w]
                    pooled_output[n, c, h, w] = np.mean(window)
    for n in range(N):
        for c in range(C):
            for h in range(out_h):
                for w in range(out_w):
                    grad = accum_grad[n, c, h, w] / (p_h * p_w)
                    grad_input[n, c, h * s_h:h * s_h + p_h, w * s_w:w * s_w + p_w] += grad
    pooled_output = np.round(pooled_output, 4).tolist()
    grad_input = np.round(grad_input, 4).tolist()
    return (pooled_output, grad_input)

