def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs // 2
    min_mel = 2595 * np.log10(1 + min_freq / 700)
    max_mel = 2595 * np.log10(1 + max_freq / 700)
    mel_points = np.linspace(min_mel, max_mel, n_filters + 2)
    freq_points = 700 * (10 ** (mel_points / 2595) - 1)
    bins = np.floor((N + 1) * freq_points / fs)
    filterbank = np.zeros((n_filters, N // 2 + 1))
    for i in range(n_filters):
        left_slopes = (bins[i + 1] - bins[i]) / (mel_points[i + 1] - mel_points[i])
        right_slopes = (bins[i + 2] - bins[i + 1]) / (mel_points[i + 2] - mel_points[i + 1])
        for k in range(int(bins[i]), int(bins[i + 1])):
            filterbank[i, k] = max(0, (k - bins[i]) / (bins[i + 1] - bins[i])) * left_slopes
        for k in range(int(bins[i + 1]), int(bins[i + 2])):
            filterbank[i, k] = max(0, (bins[i + 2] - k) / (bins[i + 2] - bins[i + 1])) * right_slopes
    if normalize:
        for i in range(n_filters):
            filterbank[i, :] *= 2 / (freq_points[i + 2] - freq_points[i])
    return np.round(filterbank, 4).tolist()

