def conv2D(X: 'np.ndarray', W: 'np.ndarray', stride: int, pad, dilation: int=0):
    (n_ex, in_rows, in_cols, in_ch) = X.shape
    (kernel_rows, kernel_cols, _, out_ch) = W.shape
    if isinstance(pad, int):
        pad_top = pad_bottom = pad_left = pad_right = pad
    elif isinstance(pad, tuple):
        (pad_top, pad_bottom, pad_left, pad_right) = pad
    else:
        raise ValueError('Invalid padding specification')
    out_rows = (in_rows + np.sum([pad_top, pad_bottom]) - dilation * (kernel_rows - 1) - 1) // stride + 1
    out_cols = (in_cols + np.sum([pad_left, pad_right]) - dilation * (kernel_cols - 1) - 1) // stride + 1
    X_padded = np.pad(X, ((0, 0), (pad_top, pad_bottom), (pad_left, pad_right), (0, 0)))
    Y = np.zeros((n_ex, out_rows, out_cols, out_ch))
    for i in range(out_rows):
        for j in range(out_cols):
            for k in range(out_ch):
                Y[:, i, j, k] = np.sum(X_padded[:, i * stride:i * stride + kernel_rows:dilation + 1, j * stride:j * stride + kernel_cols:dilation + 1, :] * W[::-1, ::-1, :, k], axis=(1, 2, 3))
    return Y.tolist()

