class GradientBoostingRegressor:

    def __init__(self, n_estimators=10, learning_rate=0.1, max_depth=3):
        self.n_estimators = n_estimators
        self.learning_rate = learning_rate
        self.max_depth = max_depth
        self.trees = []

    def fit(self, X, y):
        y_pred = np.full(np.shape(y), np.mean(y))
        for _ in range(self.n_estimators):
            gradient = y - y_pred
            tree = self._build_tree(X, gradient, self.max_depth)
            y_pred += np.multiply(self.learning_rate, self._predict(X, tree))
            self.trees.append(tree)

    def _build_tree(self, X, y, depth):
        (n_samples, n_features) = X.shape
        (best_feature, best_threshold, min_error, left_indices, right_indices) = self._find_best_split(X, y)
        if depth == 0 or min_error == 0:
            value = np.mean(y)
            return Node(value=value)
        left = self._build_tree(X[left_indices, :], y[left_indices], depth - 1)
        right = self._build_tree(X[right_indices, :], y[right_indices], depth - 1)
        return Node(best_feature, best_threshold, left, right)

    def _find_best_split(self, X, y):
        (n_samples, n_features) = X.shape
        (best_feature, best_threshold, min_error, left_indices, right_indices) = (None, None, np.inf, None, None)
        for feature in range(n_features):
            thresholds = np.unique(X[:, feature])
            for threshold in thresholds:
                left_indices = X[:, feature] < threshold
                right_indices = X[:, feature] >= threshold
                if len(np.unique(y[left_indices])) == 1 and len(np.unique(y[right_indices])) == 1:
                    continue
                error = self._mse(y[left_indices]) + self._mse(y[right_indices])
                if error < min_error:
                    min_error = error
                    best_feature = feature
                    best_threshold = threshold
                    best_left_indices = left_indices
                    best_right_indices = right_indices
        return (best_feature, best_threshold, min_error, best_left_indices, best_right_indices)

    def _mse(self, y):
        return np.mean(np.square(y - np.mean(y)))

    def _predict(self, X, tree):
        if tree.value is not None:
            return np.full(np.shape(X)[0], tree.value)
        if X[:, tree.feature] < tree.threshold:
            return self._predict(X, tree.left)
        else:
            return self._predict(X, tree.right)

    def predict(self, X):
        y_pred = np.zeros(np.shape(X)[0])
        for tree in self.trees:
            y_pred += np.multiply(self.learning_rate, self._predict(X, tree))
        return y_pred.tolist()
def gbdt_regression(X_train, y_train, X_test, n_estimators=10, learning_rate=0.1, max_depth=3):
    model = GradientBoostingRegressor(n_estimators, learning_rate, max_depth)
    model.fit(X_train, y_train)
    y_pred = model.predict(X_test)
    return [round(x, 4) for x in y_pred]

