def rbf_kernel(X: List[List[Union[int, float]]], Y: List[List[Union[int, float]]]=None, sigma: Union[float, List[float]]=None) -> Union[List[List[float]], int]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if isinstance(sigma, list):
        if len(sigma) != len(X[0]):
            return -1
        if any((s <= 0 for s in sigma)):
            return -1
    elif isinstance(sigma, float):
        if sigma <= 0:
            return -1
        sigma = [sigma] * len(X[0])
    elif sigma is None:
        sigma = [np.sqrt(len(X[0]) / 2)] * len(X[0])
    if Y is None:
        Y = X
    elif len(X[0]) != len(Y[0]):
        return -1
    X = np.array(X)
    Y = np.array(Y)
    kernel_matrix = np.zeros((X.shape[0], Y.shape[0]))
    for i in range(X.shape[0]):
        for j in range(Y.shape[0]):
            kernel_matrix[i, j] = np.exp(-0.5 * np.sum(((X[i] - Y[j]) / sigma) ** 2))
    return np.round(kernel_matrix, 4).tolist()

