import numpy as np
def best_split(X: np.ndarray, g: np.ndarray, h: np.ndarray, gamma: float, lam: float) -> tuple[int, float] | None:

    def loss_func(g, h, gamma, lam):
        return -0.5 * (g ** 2 / (h + lam)) + gamma
    overall_loss = loss_func(np.sum(g), np.sum(h), gamma, lam)
    best_gain = -np.inf
    best_split = None
    for f in range(X.shape[1]):
        for threshold in np.unique(X[:, f]):
            left_indices = np.where(X[:, f] <= threshold)[0]
            right_indices = np.where(X[:, f] > threshold)[0]
            if len(left_indices) < 2 or len(right_indices) < 2:
                continue
            left_loss = loss_func(np.sum(g[left_indices]), np.sum(h[left_indices]), gamma, lam)
            right_loss = loss_func(np.sum(g[right_indices]), np.sum(h[right_indices]), gamma, lam)
            gain = overall_loss - left_loss - right_loss
            if gain > best_gain:
                best_gain = gain
                best_split = (f, threshold)
    if best_gain > 0:
        return best_split
    else:
        return None

