def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    corpus_counter = Counter(corpus)
    sequence_counter = Counter(sequence)
    total_log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        if N == 1:
            count_sequence = sequence_counter[tuple(sequence[i:i + N])]
            count_corpus = corpus_counter[sequence[i]]
        else:
            count_sequence = sequence_counter[tuple(sequence[i:i + N])]
            count_corpus = corpus_counter[tuple(sequence[i:i + N - 1])]
        if count_corpus == 0:
            return float('-inf')
        prob = count_sequence / count_corpus
        if prob == 0:
            return float('-inf')
        total_log_prob += np.log2(prob)
    return round(total_log_prob, 4)

