def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_queue.append((i, j))
    agent_queue = deque([(0, 0)])
    fire_visited = [[0] * n for _ in range(n)]
    agent_visited = [[0] * n for _ in range(n)]
    agent_visited[0][0] = 1
    while fire_queue or agent_queue:
        for _ in range(len(fire_queue)):
            (x, y) = fire_queue.popleft()
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#') and (not fire_visited[nx][ny]):
                    fire_visited[nx][ny] = 1
                    fire_queue.append((nx, ny))
        for _ in range(len(agent_queue)):
            (x, y) = agent_queue.popleft()
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if nx == n - 1 and ny == n - 1:
                    return agent_visited[x][y]
                if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#') and (not fire_visited[nx][ny]) and (not agent_visited[nx][ny]):
                    agent_visited[nx][ny] = agent_visited[x][y] + 1
                    agent_queue.append((nx, ny))
    return -1

