def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""

    def gini(y):
        (_, counts) = np.unique(y, return_counts=True)
        probs = counts / y.size
        return 1 - np.sum(np.square(probs))

    def split(X, y, feature, threshold):
        (left, right) = (X[:, feature] <= threshold, X[:, feature] > threshold)
        return ((X[left], y[left]), (X[right], y[right]))

    def best_split(X, y):
        (best_gain, best_feature, best_threshold) = (0, None, None)
        parent_gini = gini(y)
        for feature in range(X.shape[1]):
            thresholds = np.unique(X[:, feature])
            for threshold in thresholds:
                ((_, left_y), (_, right_y)) = split(X, y, feature, threshold)
                if left_y.size == 0 or right_y.size == 0:
                    continue
                child_gini = (left_y.size * gini(left_y) + right_y.size * gini(right_y)) / y.size
                gain = parent_gini - child_gini
                if gain > best_gain:
                    (best_gain, best_feature, best_threshold) = (gain, feature, threshold)
        return (best_feature, best_threshold)

    def build_tree(X, y, depth=0):
        if np.all(y == y[0]) or (max_depth is not None and depth == max_depth):
            return lambda _: np.argmax(np.bincount(y))
        (feature, threshold) = best_split(X, y)
        if feature is None:
            return lambda _: np.argmax(np.bincount(y))
        ((X_left, y_left), (X_right, y_right)) = split(X, y, feature, threshold)
        return lambda x: build_tree(X_left, y_left, depth + 1)(x) if x[feature] <= threshold else build_tree(X_right, y_right, depth + 1)(x)
    tree = build_tree(X_train, y_train)
    return [tree(x) for x in X_test]
