import numpy as np
def metric(name: str):

    def euclidean(x, y):
        x = np.array(x, dtype=float)
        y = np.array(y, dtype=float)
        return round(np.sqrt(np.sum((x - y) ** 2)), 4)

    def manhattan(x, y):
        x = np.array(x, dtype=float)
        y = np.array(y, dtype=float)
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        x = np.array(x, dtype=float)
        y = np.array(y, dtype=float)
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        (x, y) = (np.array(x, dtype=float), np.array(y, dtype=float))
        (norm_x, norm_y) = (np.linalg.norm(x), np.linalg.norm(y))
        if norm_x == 0 or norm_y == 0:
            return -1
        return round(1 - np.dot(x, y) / (norm_x * norm_y), 4)
    if name == 'euclidean':
        return euclidean
    elif name == 'manhattan':
        return manhattan
    elif name == 'chebyshev':
        return chebyshev
    elif name == 'cosine':
        return cosine
    else:
        return lambda x, y: -1

