def apriori_frequent_itemsets(transactions: list[list], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    candidate_itemsets = []
    for transaction in transactions:
        for item in transaction:
            if [item] not in candidate_itemsets:
                candidate_itemsets.append([item])
    frequent_itemsets = []
    for itemset in candidate_itemsets:
        count = 0
        for transaction in transactions:
            if itemset[0] in transaction:
                count += 1
        if count / len(transactions) >= min_sup:
            frequent_itemsets.append(tuple(itemset))
    k = 2
    while len(frequent_itemsets) >= k - 1:
        candidate_itemsets = []
        for i in range(len(frequent_itemsets)):
            for j in range(i + 1, len(frequent_itemsets)):
                if frequent_itemsets[i][:k - 2] == frequent_itemsets[j][:k - 2]:
                    candidate_itemsets.append(frequent_itemsets[i] + (frequent_itemsets[j][-1],))
        frequent_itemsets = []
        for itemset in candidate_itemsets:
            count = 0
            for transaction in transactions:
                if all((item in transaction for item in itemset)):
                    count += 1
            if count / len(transactions) >= min_sup:
                frequent_itemsets.append(itemset)
        k += 1
    frequent_itemsets.sort(key=lambda itemset: (len(itemset), itemset))
    return frequent_itemsets

