def gaussian_naive_bayes(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray) -> list[int]:
    eps = 1e-09
    classes = np.unique(y_train)
    means = {i: {} for i in classes}
    stds = {i: {} for i in classes}
    priors = {i: 0 for i in classes}
    for c in classes:
        X_class = X_train[y_train == c]
        n_class = X_class.shape[0]
        priors[c] = n_class / len(y_train)
        for i in range(X_class.shape[1]):
            means[c][i] = np.mean(X_class[:, i])
            stds[c][i] = np.std(X_class[:, i]) + eps
    y_pred = []
    for x in X_test:
        posteriors = []
        for c in classes:
            likelihood = 1
            for (i, feature) in enumerate(x):
                mean = means[c][i]
                std = stds[c][i]
                likelihood *= 1 / np.sqrt(2 * np.pi * std ** 2) * np.exp(-(feature - mean) ** 2 / (2 * std ** 2))
            posterior = likelihood * priors[c]
            posteriors.append(posterior)
        y_pred.append(classes[np.argmax(posteriors)])
    return y_pred

import numpy as np
def spectral_clustering(data: np.ndarray, n_cluster: int, gamma: float=2.0, method: str='unnormalized') -> list[int]:
    pairwise_sq_dists = np.sum(data ** 2, axis=1).reshape(-1, 1) + np.sum(data ** 2, axis=1) - 2 * np.dot(data, data.T)
    similarity = np.exp(-gamma * pairwise_sq_dists)
    if method == 'unnormalized':
        degree = np.sum(similarity, axis=0)
        laplacian = np.diag(degree) - similarity
    elif method == 'normalized':
        sqrt_degree = np.diag(1.0 / np.sqrt(np.sum(similarity, axis=0)))
        laplacian = np.eye(similarity.shape[0]) - np.dot(np.dot(sqrt_degree, similarity), sqrt_degree)
    else:
        raise ValueError('Invalid method parameter.')
    (eigenvalues, eigenvectors) = np.linalg.eigh(laplacian)
    eigenvectors = eigenvectors[:, np.argsort(eigenvalues)]
    if method == 'normalized':
        row_sums = np.linalg.norm(eigenvectors, axis=1, ord=2)
        eigenvectors = eigenvectors / row_sums[:, np.newaxis]
    centroids = eigenvectors[:n_cluster]
    labels = np.argmin(np.linalg.norm(eigenvectors[:, np.newaxis, :] - centroids, axis=-1), axis=1)
    return labels.tolist()

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
