def choose_best_feature(X: np.ndarray, y: np.ndarray) -> tuple[int, float]:

    def entropy(labels):
        (_, counts) = np.unique(labels, return_counts=True)
        probabilities = counts / counts.sum()
        return -(probabilities * np.log2(probabilities)).sum()

    def conditional_entropy(feature, labels):
        unique_values = np.unique(feature)
        total_entropy = 0
        for value in unique_values:
            subset_labels = labels[feature == value]
            subset_entropy = entropy(subset_labels)
            total_entropy += subset_entropy * len(subset_labels) / len(labels)
        return total_entropy

    def information_gain(feature, labels):
        return entropy(labels) - conditional_entropy(feature, labels)
    best_feature_index = None
    max_information_gain = -math.inf
    for feature_index in range(X.shape[1]):
        feature = X[:, feature_index]
        ig = information_gain(feature, y)
        if ig > max_information_gain:
            max_information_gain = ig
            best_feature_index = feature_index
    return (best_feature_index, round(max_information_gain, 6))

