def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if n_samples < sample_size:
        sample_size = n_samples

    def build_tree(data, height_limit=0):
        if height_limit == 0:
            height_limit = math.ceil(math.log2(sample_size))
        if len(data) <= 1 or height_limit == 0:
            return {'size': len(data)}
        feature_idx = np.random.choice(n_features)
        threshold = np.random.uniform(low=np.min(data[:, feature_idx]), high=np.max(data[:, feature_idx]))
        left_tree = build_tree(data[data[:, feature_idx] < threshold], height_limit - 1)
        right_tree = build_tree(data[data[:, feature_idx] >= threshold], height_limit - 1)
        return {'feature': feature_idx, 'threshold': threshold, 'left': left_tree, 'right': right_tree}

    def path_length(tree, x, path=0):
        if 'size' in tree:
            if tree['size'] == 1:
                return path
            else:
                return path + c(tree['size'])
        if x[tree['feature']] < tree['threshold']:
            return path_length(tree['left'], x, path + 1)
        else:
            return path_length(tree['right'], x, path + 1)

    def c(n):
        if n == 1:
            return 0
        else:
            return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def score(x, trees):
        return sum((path_length(tree, x) for tree in trees)) / n_trees
    trees = [build_tree(data[np.random.choice(n_samples, sample_size, replace=False)]) for _ in range(n_trees)]
    scores = [score(x, trees) for x in data]
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    s_scores = [2 ** (-score / phi) for score in scores]
    outliers = np.argsort(s_scores)[::-1][:math.ceil(epsilon * n_samples)]
    return sorted(outliers)

