def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    empirical_distributions = defaultdict(lambda : defaultdict(int))
    for (x, y) in zip(X_train, y_train):
        empirical_distributions[y][tuple(x)] += 1
    weights = np.zeros(len(empirical_distributions[y_train[0]]))
    for _ in range(n_iter):
        weight_updates = np.zeros_like(weights)
        for (y, x_counts) in empirical_distributions.items():
            for (x, count) in x_counts.items():
                exponent = np.dot(weights, x)
                for i in range(len(weights)):
                    weight_updates[i] += count * x[i] * math.exp(exponent)
        weights += weight_updates / len(X_train)
        if np.max(np.abs(weight_updates)) < epsilon:
            break
    y_pred = []
    for x in X_test:
        log_probabilities = defaultdict(float)
        for (y, x_counts) in empirical_distributions.items():
            for (x_train, count) in x_counts.items():
                log_probabilities[y] += count * np.dot(weights, x_train)
        y_pred.append(max(log_probabilities, key=log_probabilities.get))
    return y_pred
