class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.optimizer = None

    def initialize(self, optimizer):
        input_shape = self.input_shape
        limit = 1 / math.sqrt(input_shape[0])
        self.W = np.random.uniform(-limit, limit, (input_shape[0], self.n_units))
        self.w0 = np.zeros((1, self.n_units))
        self.optimizer = copy.copy(optimizer)

    def parameters(self):
        return np.prod(self.W.shape) + np.prod(self.w0.shape)

    def forward_pass(self, X, training):
        self.layer_input = X
        return X.dot(self.W) + self.w0

    def backward_pass(self, accum_grad):
        w = self.W
        if self.trainable:
            grad_w = self.layer_input.T.dot(accum_grad)
            grad_w0 = np.sum(accum_grad, axis=0, keepdims=True)
            self.optimizer.update(self.W, grad_w)
            self.optimizer.update(self.w0, grad_w0)
        accum_grad = accum_grad.dot(w.T)
        return accum_grad

    def output_shape(self):
        return (self.n_units,)
