import numpy as np
def create_hypervector(dim, seed):
    """Generate a random hypervector of a given dimension with a fixed seed."""
    np.random.seed(seed)
    return np.where(np.random.rand(dim) > 0.5, 1, -1)
def bind_hypervectors(hv1, hv2):
    """Bind two hypervectors using element-wise multiplication."""
    return hv1 * hv2
def bundle_hypervectors(hvs):
    """Bundle a list of hypervectors by element-wise addition and normalization."""
    bundled = np.sum(hvs, axis=0)
    return np.where(bundled > 0, 1, -1)
def create_row_hv(row, dim, random_seeds):
    """Generate a composite hypervector for a dataset row using HDC."""
    feature_hvs = []
    for (feature, value) in row.items():
        feature_hv = create_hypervector(dim, random_seeds[feature])
        value_hv = create_hypervector(dim, random_seeds[feature] + hash(value))
        bound_hv = bind_hypervectors(feature_hv, value_hv)
        feature_hvs.append(bound_hv)
    composite_hv = bundle_hypervectors(feature_hvs)
    return composite_hv.tolist()
