from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    """
    Builds a decision tree from the given examples and attributes.

    :param examples: A list of examples, where each example is a dictionary of attribute-value pairs.
    :param attributes: A list of attribute names.
    :param target_attr: The name of the target attribute for classification.
    :return: A nested dictionary representing the decision tree.
    """

    def entropy(examples, target_attr):
        """Calculate the entropy of the target attribute."""
        counts = Counter([example[target_attr] for example in examples])
        total = len(examples)
        return -sum((count / total * math.log2(count / total) for count in counts.values() if count > 0))

    def information_gain(examples, attribute, target_attr):
        """Calculate the information gain of a given attribute."""
        total_entropy = entropy(examples, target_attr)
        attribute_values = set([example[attribute] for example in examples])
        weighted_entropy = 0
        for value in attribute_values:
            subset = [example for example in examples if example[attribute] == value]
            weighted_entropy += len(subset) / len(examples) * entropy(subset, target_attr)
        return total_entropy - weighted_entropy

    def split_examples(examples, attribute):
        """Split examples based on the attribute values."""
        splits = {}
        for example in examples:
            value = example[attribute]
            if value not in splits:
                splits[value] = []
            splits[value].append(example)
        return splits

    def most_common_value(examples, target_attr):
        """Find the most common value of the target attribute."""
        counts = Counter([example[target_attr] for example in examples])
        return counts.most_common(1)[0][0]
    if not examples:
        return most_common_value(examples, target_attr)
    if len(set([example[target_attr] for example in examples])) == 1:
        return examples[0][target_attr]
    if not attributes:
        return most_common_value(examples, target_attr)
    best_attribute = max(attributes, key=lambda attribute: information_gain(examples, attribute, target_attr))
    tree = {best_attribute: {}}
    for (value, subset) in split_examples(examples, best_attribute).items():
        if not subset:
            tree[best_attribute][value] = most_common_value(examples, target_attr)
        else:
            remaining_attributes = [attr for attr in attributes if attr != best_attribute]
            subtree = learn_decision_tree(subset, remaining_attributes, target_attr)
            tree[best_attribute][value] = subtree
    return tree
