def kernel_matrix(data, kernel='linear', sigma=1.0):
    n_samples = data.shape[0]
    K = np.zeros((n_samples, n_samples))
    if kernel == 'linear':
        K = np.dot(data, data.T)
    elif kernel == 'rbf':
        for i in range(n_samples):
            for j in range(n_samples):
                diff = data[i] - data[j]
                K[i, j] = np.exp(-np.dot(diff, diff) / (2 * sigma ** 2))
    return K
def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    (n_samples, n_features) = data.shape
    alphas = np.zeros(n_samples)
    bias = 0.0
    K = kernel_matrix(data, kernel, sigma)
    for t in range(1, iterations + 1):
        lambda_t = 1 / (lambda_val * t)
        for i in range(n_samples):
            if labels[i] * (np.sum(alphas * labels * K[:, i]) + bias) < 1:
                alphas[i] = (1 - lambda_t * lambda_val) * alphas[i] + lambda_t * labels[i]
            else:
                alphas[i] = (1 - lambda_t * lambda_val) * alphas[i]
        bias = bias + lambda_t * (1 - labels * (np.sum(alphas * labels * K, axis=1) + bias))
    return (alphas.round(4).tolist(), round(bias, 4))

