def sigmoid(x):
    """Sigmoid activation function."""
    return 1 / (1 + np.exp(-x))
def sigmoid_derivative(x):
    """Derivative of the sigmoid function."""
    return sigmoid(x) * (1 - sigmoid(x))
def mse_loss(y_true, y_pred):
    """Mean Squared Error loss function."""
    return np.mean((y_true - y_pred) ** 2)
def train_neuron(features: np.ndarray, labels: np.ndarray, initial_weights: np.ndarray, initial_bias: float, learning_rate: float, epochs: int) -> (np.ndarray, float, list[float]):
    weights = initial_weights
    bias = initial_bias
    mse_values = []
    for _ in range(epochs):
        predictions = sigmoid(np.dot(features, weights) + bias)
        mse = mse_loss(labels, predictions)
        mse_values.append(round(mse, 4))
        error = predictions - labels
        gradient_weights = np.dot(features.T, error * sigmoid_derivative(predictions))
        gradient_bias = np.sum(error * sigmoid_derivative(predictions))
        weights -= learning_rate * gradient_weights
        bias -= learning_rate * gradient_bias
    return (weights.tolist(), round(bias, 4), mse_values)
