import numpy as np
def rref(matrix):
    """
    Converts a given matrix into its Reduced Row Echelon Form (RREF).

    :param matrix: A 2D numpy array representing the matrix to be converted.
    :return: A 2D list representing the RREF of the input matrix.
    """
    matrix = np.array(matrix, dtype=float)
    (row_count, col_count) = matrix.shape
    lead = 0
    for r in range(row_count):
        if lead >= col_count:
            return matrix.tolist()
        i = r
        while matrix[i, lead] == 0:
            i += 1
            if i == row_count:
                i = r
                lead += 1
                if lead == col_count:
                    return matrix.tolist()
        matrix[[i, r]] = matrix[[r, i]]
        pivot = matrix[r, lead]
        matrix[r] = matrix[r] / pivot
        for i in range(row_count):
            if i != r:
                matrix[i] = matrix[i] - matrix[i, lead] * matrix[r]
        lead += 1
    return matrix.tolist()

