def calculate_brightness(img):
    """
    Calculates the average brightness of a grayscale image.

    Parameters:
    img (list of list of int): A 2D matrix representing the grayscale image.

    Returns:
    float: The average brightness of the image rounded to two decimal places, or -1 for invalid input.
    """
    if not img or not all((isinstance(row, list) and len(row) == len(img[0]) for row in img)):
        return -1
    total_brightness = 0
    pixel_count = 0
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
            total_brightness += pixel
            pixel_count += 1
    if pixel_count == 0:
        return -1
    average_brightness = total_brightness / pixel_count
    return round(average_brightness, 2)

