    x = token_embeddings + position_embeddings
    attention_weights = np.random.rand(hparams['n_head'], x.shape[0], x.shape[0])
    attention_output = np.tensordot(attention_weights, x, axes=([2], [1]))
    ff_output = np.tanh(np.dot(attention_output, np.random.rand(10, 10)))
    mean = np.mean(ff_output, axis=-1, keepdims=True)
