def matrixmul(a: list[list[int | float]], b: list[list[int | float]]) -> list[list[int | float]] | int:
    """
    Multiplies two matrices a and b.
    Returns the resulting matrix if multiplication is possible, otherwise returns -1.
    """
    if len(a[0]) != len(b):
        return -1
    result = [[0 for _ in range(len(b[0]))] for _ in range(len(a))]
    for i in range(len(a)):
        for j in range(len(b[0])):
            for k in range(len(b)):
                result[i][j] += a[i][k] * b[k][j]
    return result

import numpy as np
def kernel_matrix(data, kernel='linear', sigma=1.0):
    """Compute the kernel matrix based on the specified kernel type."""
    n_samples = data.shape[0]
    kernel_matrix = np.zeros((n_samples, n_samples))
    if kernel == 'linear':
        kernel_matrix = np.dot(data, data.T)
    elif kernel == 'rbf':
        for i in range(n_samples):
            for j in range(n_samples):
                diff = data[i] - data[j]
                kernel_matrix[i, j] = np.exp(-np.dot(diff, diff) / (2 * sigma ** 2))
    return kernel_matrix
def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    """Deterministic Pegasos Kernel SVM implementation."""
    (n_samples, n_features) = data.shape
    alpha = np.zeros(n_samples)
    kernel_mat = kernel_matrix(data, kernel, sigma)
    for t in range(1, iterations + 1):
        lambda_t = 1 / (lambda_val * t)
        for i in range(n_samples):
            if labels[i] * (np.sum(alpha * labels * kernel_mat[:, i]) - alpha[i]) < 1:
                alpha[i] = (1 - lambda_t * lambda_val) * alpha[i] + lambda_t * labels[i]
            else:
                alpha[i] = (1 - lambda_t * lambda_val) * alpha[i]
    support_indices = np.where(alpha > 0)[0]
    bias = 0
    for i in support_indices:
        bias += labels[i] - np.sum(alpha * labels * kernel_mat[support_indices, i])
    bias /= len(support_indices)
    return (alpha.tolist(), round(bias, 4))

import numpy as np
def sigmoid(x):
    """Sigmoid activation function."""
    return 1 / (1 + np.exp(-x))
def sigmoid_derivative(x):
    """Derivative of the sigmoid function."""
    return sigmoid(x) * (1 - sigmoid(x))
def mse_loss(predictions, labels):
    """Mean Squared Error loss function."""
    return np.mean((predictions - labels) ** 2)
def train_neuron(features: np.ndarray, labels: np.ndarray, initial_weights: np.ndarray, initial_bias: float, learning_rate: float, epochs: int) -> (np.ndarray, float, list[float]):
    weights = initial_weights
    bias = initial_bias
    mse_values = []
    for epoch in range(epochs):
        predictions = sigmoid(np.dot(features, weights) + bias)
        mse = mse_loss(predictions, labels)
        mse_values.append(round(mse, 4))
        error = predictions - labels
        gradient_weights = np.dot(features.T, error * sigmoid_derivative(predictions))
        gradient_bias = np.sum(error * sigmoid_derivative(predictions))
        weights -= learning_rate * gradient_weights
        bias -= learning_rate * gradient_bias
    return (weights.tolist(), round(bias, 4), mse_values)
