def OSA(source: str, target: str) -> int:
    """
    Calculates the Optimal String Alignment (OSA) distance between two strings.
    The OSA distance is the minimum number of edits (insert, delete, substitute, transpose)
    required to transform one string into another.
    """
    (len_source, len_target) = (len(source), len(target))
    dp = np.zeros((len_source + 1, len_target + 1), dtype=int)
    for i in range(len_source + 1):
        dp[i][0] = i
    for j in range(len_target + 1):
        dp[0][j] = j
    for i in range(1, len_source + 1):
        for j in range(1, len_target + 1):
            if source[i - 1] == target[j - 1]:
                cost = 0
            else:
                cost = 1
            dp[i][j] = min(dp[i - 1][j] + 1, dp[i][j - 1] + 1, dp[i - 1][j - 1] + cost)
            if i > 1 and j > 1 and (source[i - 1] == target[j - 2]) and (source[i - 2] == target[j - 1]):
                dp[i][j] = min(dp[i][j], dp[i - 2][j - 2] + 1)
    return dp[len_source][len_target]
