def pos_encoding(position: int, d_model: int):
    """
    Calculates the positional encodings for a given sequence length and model dimensionality.
    If position is 0 or d_model is less than or equal to 0, returns -1.

    :param position: The length of the sequence.
    :param d_model: The dimensionality of the model.
    :return: A list of positional encodings or -1 if input is invalid.
    """
    if position == 0 or d_model <= 0:
        return -1
    pe = np.zeros((position, d_model), dtype=np.float16)
    for pos in range(position):
        for i in range(0, d_model, 2):
            pe[pos, i] = np.sin(pos / 10000 ** (2 * i / d_model))
            if i + 1 < d_model:
                pe[pos, i + 1] = np.cos(pos / 10000 ** (2 * (i + 1) / d_model))
    return pe.tolist()

def model_fit_quality(training_accuracy, test_accuracy):
    """
    Determine if the model is overfitting, underfitting, or a good fit based on training and test accuracy.
    :param training_accuracy: float, training accuracy of the model (0 <= training_accuracy <= 1)
    :param test_accuracy: float, test accuracy of the model (0 <= test_accuracy <= 1)
    :return: int, one of '1', '-1', or '0'.
    """
    if training_accuracy - test_accuracy > 0.2:
        return 1
    elif training_accuracy < 0.7 and test_accuracy < 0.7:
        return -1
    else:
        return 0

