from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    """
    Builds a decision tree from the given examples and attributes.

    :param examples: List of examples, each example is a dict of attribute-value pairs.
    :param attributes: List of attribute names.
    :param target_attr: The name of the target attribute.
    :return: A nested dictionary representing the decision tree.
    """

    def entropy(examples, target_attr):
        """Calculate the entropy of the target attribute."""
        counter = Counter([example[target_attr] for example in examples])
        total = len(examples)
        return -sum((count / total * math.log2(count / total) for count in counter.values()))

    def information_gain(examples, attribute, target_attr):
        """Calculate the information gain of a split on the attribute."""
        total_entropy = entropy(examples, target_attr)
        partitions = {}
        for example in examples:
            value = example[attribute]
            if value not in partitions:
                partitions[value] = []
            partitions[value].append(example)
        weighted_entropy = sum((len(partition) / len(examples) * entropy(partition, target_attr) for partition in partitions.values()))
        return total_entropy - weighted_entropy

    def split(examples, attribute):
        """Split the examples based on the attribute."""
        partitions = {}
        for example in examples:
            value = example[attribute]
            if value not in partitions:
                partitions[value] = []
            partitions[value].append(example)
        return partitions

    def most_common_value(examples, target_attr):
        """Find the most common value of the target attribute in the examples."""
        counter = Counter([example[target_attr] for example in examples])
        return counter.most_common(1)[0][0]
    if not examples:
        return {}
    if len(set([example[target_attr] for example in examples])) == 1:
        return examples[0][target_attr]
    if not attributes:
        return most_common_value(examples, target_attr)
    best_attribute = max(attributes, key=lambda attr: information_gain(examples, attr, target_attr))
    tree = {best_attribute: {}}
    for (value, subset) in split(examples, best_attribute).items():
        remaining_attributes = [attr for attr in attributes if attr != best_attribute]
        subtree = learn_decision_tree(subset, remaining_attributes, target_attr)
        tree[best_attribute][value] = subtree
    return tree
