import numpy as np
def kernel_matrix(X, kernel='linear', sigma=1.0):
    n = X.shape[0]
    K = np.zeros((n, n))
    if kernel == 'linear':
        K = X @ X.T
    elif kernel == 'rbf':
        for i in range(n):
            for j in range(n):
                diff = X[i] - X[j]
                K[i, j] = np.exp(-np.dot(diff, diff) / (2 * sigma ** 2))
    return K
def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    (n_samples, n_features) = data.shape
    alpha = np.zeros(n_samples)
    K = kernel_matrix(data, kernel, sigma)
    for t in range(1, iterations + 1):
        lambda_t = 1 / (lambda_val * t)
        for i in range(n_samples):
            if labels[i] * (np.sum(alpha * labels * K[:, i]) - alpha[i]) < 1:
                alpha[i] = (1 - lambda_t * lambda_val) * alpha[i] + lambda_t * labels[i]
            else:
                alpha[i] = (1 - lambda_t * lambda_val) * alpha[i]
    alpha = np.round(alpha, 4)
    bias = 0
    support_indices = np.where(alpha > 1e-05)[0]
    for i in support_indices:
        bias += labels[i] - np.sum(alpha * labels * K[:, i])
    bias /= len(support_indices)
    bias = np.round(bias, 4)
    return (alpha.tolist(), bias)
