class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.optimizer_W = None
        self.optimizer_w0 = None

    def initialize(self, optimizer):
        limit = 1 / math.sqrt(self.input_shape[0])
        self.W = np.random.uniform(-limit, limit, (self.input_shape[0], self.n_units))
        self.w0 = np.zeros((1, self.n_units))
        self.optimizer_W = copy.deepcopy(optimizer)
        self.optimizer_w0 = copy.deepcopy(optimizer)

    def parameters(self):
        return np.prod(self.W.shape) + np.prod(self.w0.shape)

    def forward_pass(self, X, training):
        self.layer_input = X
        return X.dot(self.W) + self.w0

    def backward_pass(self, accum_grad):
        w_grad = self.layer_input.T.dot(accum_grad)
        if self.trainable:
            self.W = self.optimizer_W.update(self.W, w_grad)
            self.w0 = self.optimizer_w0.update(self.w0, np.sum(accum_grad, axis=0, keepdims=True))
        return accum_grad.dot(self.W.T)

    def output_shape(self):
        return (self.n_units,)
