import numpy as np
def rref(matrix):
    """
    Converts a given matrix to its Reduced Row Echelon Form (RREF).

    Parameters:
    matrix (list of lists): A 2D list representing the matrix to be converted.

    Returns:
    list of lists: The RREF of the given matrix.
    """
    mat = np.array(matrix, dtype=float)
    (rows, cols) = mat.shape
    lead = 0
    for r in range(rows):
        if cols <= lead:
            return mat.tolist()
        i = r
        while mat[i, lead] == 0:
            i += 1
            if rows == i:
                i = r
                lead += 1
                if cols == lead:
                    return mat.tolist()
        mat[[i, r]] = mat[[r, i]]
        mat[r] = mat[r] / mat[r, lead]
        for i in range(rows):
            if i != r:
                mat[i] = mat[i] - mat[i, lead] * mat[r]
        lead += 1
    return mat.tolist()
