def calculate_brightness(img):
    if not img or not img[0]:
        return -1
    total_brightness = 0
    total_pixels = 0
    row_length = len(img[0])
    for row in img:
        if len(row) != row_length:
            return -1
        for pixel in row:
            if pixel < 0 or pixel > 255:
                return -1
            total_brightness += pixel
            total_pixels += 1
    if total_pixels == 0:
        return -1
    average_brightness = total_brightness / total_pixels
    return round(average_brightness, 2)
