def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    if all((examples[0][target_attr] == example[target_attr] for example in examples)):
        return examples[0][target_attr]
    if not attributes:
        return Counter((example[target_attr] for example in examples)).most_common(1)[0][0]
    best_attr = max(attributes, key=lambda attr: information_gain(examples, attr, target_attr))
    tree = {best_attr: {}}
    for value in set((example[best_attr] for example in examples)):
        subset = [example for example in examples if example[best_attr] == value]
        if not subset:
            tree[best_attr][value] = Counter((example[target_attr] for example in examples)).most_common(1)[0][0]
        else:
            subtree = learn_decision_tree(subset, [attr for attr in attributes if attr != best_attr], target_attr)
            tree[best_attr][value] = subtree
    return tree
def entropy(examples: list[dict], target_attr: str) -> float:
    """Calculate the entropy of the examples based on the target attribute."""
    counts = Counter((example[target_attr] for example in examples))
    total = len(examples)
    return -sum((count / total * math.log2(count / total) for count in counts.values()))
def information_gain(examples: list[dict], attribute: str, target_attr: str) -> float:
    """Calculate the information gain of splitting the examples by the attribute."""
    total_entropy = entropy(examples, target_attr)
    values = set((example[attribute] for example in examples))
    weighted_entropy = sum((sum((1 for example in examples if example[attribute] == value)) / len(examples) * entropy([example for example in examples if example[attribute] == value], target_attr) for value in values))
    return total_entropy - weighted_entropy
