import numpy as np
def rref(matrix):
    """
    Converts the given matrix to its Reduced Row Echelon Form (RREF).

    Parameters:
    matrix (list of lists): The input matrix to be converted to RREF.

    Returns:
    list of lists: The RREF of the input matrix.
    """
    mat = np.array(matrix, dtype=float)
    (rows, cols) = mat.shape
    lead = 0
    for r in range(rows):
        if cols <= lead:
            return mat.tolist()
        i = r
        while mat[i, lead] == 0:
            i += 1
            if i == rows:
                i = r
                lead += 1
                if cols == lead:
                    return mat.tolist()
        mat[[i, r]] = mat[[r, i]]
        pivot = mat[r, lead]
        mat[r] = mat[r] / pivot
        for i in range(rows):
            if i != r:
                mat[i] = mat[i] - mat[i, lead] * mat[r]
        lead += 1
    return mat.tolist()
assert rref(np.array([ [1, 2, -1, -4], [2, 3, -1, -11], [-2, 0, -3, 22] ])) == [[ 1., 0., 0., -8.], [ 0., 1., 0., 1.], [-0., -0., 1., -2.]]
assert rref(np.array([ [2, 4, -2], [4, 9, -3], [-2, -3, 7] ])) == [[ 1., 0., 0.], [ 0., 1., 0.], [ 0., 0., 1.]]
assert rref(np.array([ [0, 2, -1, -4], [2, 0, -1, -11], [-2, 0, 0, 22] ])) == [[ 1., 0., 0., -11.],[-0., 1., 0., -7.5],[-0., -0., 1., -11.]]
