def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('The corpus is empty.')

    def term_frequency(doc, term):
        return doc.count(term) / len(doc)

    def inverse_document_frequency(corpus, term):
        df = sum((1 for doc in corpus if term in doc)) + 1
        return math.log(len(corpus) / df)
    tf_idf_scores = []
    for doc in corpus:
        doc_scores = []
        for term in query:
            tf = term_frequency(doc, term)
            idf = inverse_document_frequency(corpus, term)
            doc_scores.append(round(tf * idf, 4))
        tf_idf_scores.append(doc_scores)
    return np.array(tf_idf_scores).tolist()
