def create_hv(seed, dim):
    """Generate a hypervector with a given seed and dimension."""
    np.random.seed(seed)
    return np.where(np.random.rand(dim) > 0.5, 1, -1)
def bind_hvs(hv1, hv2):
    """Bind two hypervectors using element-wise multiplication and normalization."""
    return hv1 * hv2
def bundle_hvs(hvs):
    """Bundle a list of hypervectors by averaging them."""
    return np.mean(hvs, axis=0)
def create_row_hv(row, dim, random_seeds):
    """Generate a composite hypervector for a dataset row."""
    feature_hvs = []
    for (feature, value) in row.items():
        feature_hv = create_hv(hash(feature), dim)
        value_hv = create_hv(random_seeds[feature], dim)
        bound_hv = bind_hvs(feature_hv, value_hv)
        feature_hvs.append(bound_hv)
    row_hv = bundle_hvs(feature_hvs)
    return row_hv.tolist()
