def softmax(x):
    e_x = np.exp(x - np.max(x))
    return e_x / e_x.sum(axis=0)
def compute_policy_gradient(theta: np.ndarray, episodes: list[list[tuple[int, int, float]]]):
    (num_states, num_actions) = theta.shape
    gradient = np.zeros_like(theta)
    for episode in episodes:
        returns = 0
        returns_list = []
        for (_, _, reward) in reversed(episode):
            returns = 0.99 * returns + reward
            returns_list.append(returns)
        returns_list = list(reversed(returns_list))
        for (i, (state, action, _)) in enumerate(episode):
            policy = softmax(theta[state, :])
            log_policy = np.log(policy[action])
            gradient[state, action] += log_policy * returns_list[i]
            for a in range(num_actions):
                if a != action:
                    gradient[state, a] += np.log(1 - policy[action]) * returns_list[i]
    average_gradient = gradient / len(episodes)
    return np.round(average_gradient, 4).tolist()
