import numpy as np
def softplus(x, deriv: bool=False):
    """Computes the SoftPlus activation function or its derivative (sigmoid) for a given input.

    Args:
        x: A scalar, Python list, or NumPy array of real values.
        deriv: A boolean flag indicating whether to return the SoftPlus function (False) or its derivative (True).

    Returns:
        A Python list of the computed values, rounded to the nearest 4th decimal, or a single float if the input is a scalar.
    """
    x = np.array(x)
    if deriv:
        result = 1 / (1 + np.exp(-x))
    else:
        result = np.log1p(np.exp(-np.abs(x))) + np.maximum(x, 0)
    result = np.round(result, 4)
    if not isinstance(x, np.ndarray):
        result = result.tolist()
    if isinstance(x, (int, float)):
        return float(result)
    else:
        return result

