from typing import List
def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """

    def build_tree(transactions, headers):
        root = FPNode(None, None)
        for transaction in transactions:
            sorted_items = sorted(transaction, key=lambda x: headers[x], reverse=True)
            current_node = root
            for item in sorted_items:
                if item in current_node.children:
                    current_node.children[item].increment()
                else:
                    new_node = FPNode(item, current_node)
                    current_node.children[item] = new_node
                    if item in headers:
                        headers[item]['nodes'].append(new_node)
                current_node = current_node.children[item]
        return root

    def mine_tree(node, prefix, frequent_itemsets):
        for (item, header) in headers.items():
            new_prefix = prefix + [item]
            support = header['support']
            if support >= min_support:
                frequent_itemsets.append(new_prefix)
                conditional_patterns = []
                for node in header['nodes']:
                    path = []
                    while node.parent and node.item is not None:
                        path.append(node.item)
                        node = node.parent
                    if path:
                        path.reverse()
                        conditional_patterns.append((path, node.count))
                if conditional_patterns:
                    (conditional_tree, conditional_headers) = build_conditional_tree(conditional_patterns, min_support)
                    if conditional_headers:
                        mine_tree(conditional_tree, new_prefix, frequent_itemsets)

    def build_conditional_tree(patterns, min_support):
        headers = defaultdict(lambda : {'support': 0, 'nodes': []})
        for (pattern, count) in patterns:
            for item in pattern:
                headers[item]['support'] += count
        headers = {item: info for (item, info) in headers.items() if info['support'] >= min_support}
        if not headers:
            return (None, None)
        sorted_items = sorted(headers.keys(), key=lambda x: headers[x]['support'], reverse=True)
        conditional_tree = FPNode(None, None)
        for (pattern, count) in patterns:
            pattern = [item for item in pattern if item in headers]
            if not pattern:
                continue
            sorted_pattern = sorted(pattern, key=lambda x: headers[x]['support'], reverse=True)
            current_node = conditional_tree
            for item in sorted_pattern:
                if item in current_node.children:
                    current_node.children[item].increment(count)
                else:
                    new_node = FPNode(item, current_node)
                    current_node.children[item] = new_node
                    headers[item]['nodes'].append(new_node)
                current_node = current_node.children[item]
        return (conditional_tree, headers)
    if not transactions:
        return []
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    headers = {item: {'support': count, 'nodes': []} for (item, count) in frequent_items.items()}
    tree = build_tree(transactions, headers)
    frequent_itemsets = []
    mine_tree(tree, [], frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
