import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement k-Nearest Neighbors classifier."""

    def compute_distances(X_train, X_test, metric):
        if metric == 'euclidean':
            return np.sqrt(((X_train[:, np.newaxis] - X_test) ** 2).sum(axis=2))
        elif metric == 'manhattan':
            return np.abs(X_train[:, np.newaxis] - X_test).sum(axis=2)
        elif metric == 'cosine':
            dot_product = (X_train[:, np.newaxis] * X_test).sum(axis=2)
            norm_train = np.linalg.norm(X_train, axis=1)[:, np.newaxis]
            norm_test = np.linalg.norm(X_test, axis=1)
            cosine_similarity = dot_product / (norm_train * norm_test)
            return 1 - cosine_similarity
        else:
            return compute_distances(X_train, X_test, 'euclidean')
    distances = compute_distances(X_train, X_test, metric)
    nearest_indices = np.argsort(distances, axis=0)[:k]
    predictions = []
    for indices in nearest_indices.T:
        labels = y_train[indices]
        (unique_labels, counts) = np.unique(labels, return_counts=True)
        max_count = np.max(counts)
        majority_labels = unique_labels[counts == max_count]
        predictions.append(np.min(majority_labels))
    return np.array(predictions)

import numpy as np
def gini_impurity(y):
    """Calculate the Gini impurity for a set of labels."""
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def best_split(X, y, max_depth):
    """Find the best split for a node."""
    if len(np.unique(y)) == 1 or (max_depth is not None and max_depth == 0):
        return (None, np.bincount(y).argmax())
    best_gain = -1
    best_feature = None
    best_threshold = None
    parent_gini = gini_impurity(y)
    for feature in range(X.shape[1]):
        sorted_indices = np.argsort(X[:, feature])
        for i in range(1, len(sorted_indices)):
            if X[sorted_indices[i], feature] != X[sorted_indices[i - 1], feature]:
                threshold = (X[sorted_indices[i], feature] + X[sorted_indices[i - 1], feature]) / 2
                left_indices = np.where(X[:, feature] <= threshold)[0]
                right_indices = np.where(X[:, feature] > threshold)[0]
                if len(left_indices) == 0 or len(right_indices) == 0:
                    continue
                left_gini = gini_impurity(y[left_indices])
                right_gini = gini_impurity(y[right_indices])
                weighted_gini = (len(left_indices) * left_gini + len(right_indices) * right_gini) / len(y)
                gain = parent_gini - weighted_gini
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature
                    best_threshold = threshold
    if best_gain == -1:
        return (None, np.bincount(y).argmax())
    return ((best_feature, best_threshold), None)
def build_tree(X, y, max_depth=None):
    """Build a decision tree recursively."""
    (split, leaf_value) = best_split(X, y, max_depth)
    if split is None:
        return {'leaf': leaf_value}
    (feature, threshold) = split
    left_indices = np.where(X[:, feature] <= threshold)[0]
    right_indices = np.where(X[:, feature] > threshold)[0]
    left_tree = build_tree(X[left_indices], y[left_indices], None if max_depth is None else max_depth - 1)
    right_tree = build_tree(X[right_indices], y[right_indices], None if max_depth is None else max_depth - 1)
    return {'split': split, 'left': left_tree, 'right': right_tree}
def predict_single(x, tree):
    """Predict the class of a single sample."""
    if 'leaf' in tree:
        return tree['leaf']
    (feature, threshold) = tree['split']
    if x[feature] <= threshold:
        return predict_single(x, tree['left'])
    else:
        return predict_single(x, tree['right'])
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    tree = build_tree(X_train, y_train, max_depth)
    return [predict_single(x, tree) for x in X_test]

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function."""

    def validate_vectors(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        if len(x) != len(y) or len(x) == 0:
            return False
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except ValueError:
            return False
        return (x, y)

    def euclidean(x, y):
        return round(np.sqrt(np.sum((x - y) ** 2)), 4)

    def manhattan(x, y):
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        return round(1 - np.dot(x, y) / (norm_x * norm_y), 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    if name not in metrics:
        return lambda x, y: -1

    def d(x, y):
        validated = validate_vectors(x, y)
        if not validated:
            return -1
        (x, y) = validated
        return metrics[name](x, y)
    return d

def calculate_score(hand: list[int]) -> int:
    """Calculate the score of a hand in Blackjack."""
    score = sum(hand)
    aces = hand.count(1)
    while score <= 11 and aces > 0:
        score += 10
        aces -= 1
    return min(score, 22)
def is_natural_blackjack(hand: list[int]) -> bool:
    """Check if the hand is a natural blackjack."""
    return len(hand) == 2 and 1 in hand and (10 in hand)
def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score > 21:
        return -1
    elif dealer_score > 21:
        return 1
    elif player_score > dealer_score:
        if is_natural_blackjack(player):
            return 1.5
        return 1
    elif player_score < dealer_score:
        return -1
    else:
        return 0

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """

    def apply_leaky_relu(x):
        return max(x, a * x)

    def apply_recursively(data, func):
        if isinstance(data, list):
            return [apply_recursively(item, func) for item in data]
        elif isinstance(data, np.ndarray):
            return apply_recursively(data.tolist(), func)
        else:
            return func(data)
    return apply_recursively(z, apply_leaky_relu)

