def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    def mse(y):
        return np.mean((y - np.mean(y)) ** 2)

    def split_node(X, y, depth):
        if depth == max_depth or len(y) < 2:
            return np.mean(y)
        best_split = {}
        best_mse = float('inf')
        for i in range(X.shape[1]):
            for value in np.unique(X[:, i]):
                left_idx = X[:, i] <= value
                right_idx = X[:, i] > value
                if np.any(left_idx) and np.any(right_idx):
                    mse_left = mse(y[left_idx])
                    mse_right = mse(y[right_idx])
                    mse_split = (len(y[left_idx]) * mse_left + len(y[right_idx]) * mse_right) / len(y)
                    if mse_split < best_mse:
                        best_mse = mse_split
                        best_split = {'feature': i, 'value': value, 'left': split_node(X[left_idx], y[left_idx], depth + 1), 'right': split_node(X[right_idx], y[right_idx], depth + 1)}
        return best_split

    def predict_tree(x, node):
        if isinstance(node, float):
            return node
        if x[node['feature']] <= node['value']:
            return predict_tree(x, node['left'])
        else:
            return predict_tree(x, node['right'])

    def predict(X, trees):
        return np.array([sum((learning_rate * predict_tree(x, tree) for tree in trees)) for x in X])
    F = np.full_like(y_train, np.mean(y_train))
    trees = []
    for _ in range(n_estimators):
        residuals = y_train - F
        tree = split_node(X_train, residuals, 0)
        trees.append(tree)
        F += learning_rate * predict(X_train, [tree])
    predictions = predict(X_test, trees)
    return [round(float(pred), 4) for pred in predictions]

