def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    if not X_train or not y_train:
        return []

    def sse(y):
        mean_y = np.mean(y)
        return np.sum((y - mean_y) ** 2)

    def split_data(X, y, split_val):
        left_idx = X <= split_val
        right_idx = X > split_val
        return (X[left_idx], X[right_idx], y[left_idx], y[right_idx])

    def build_tree(X, y, depth=0):
        if depth == max_depth or len(X) < 2:
            return np.mean(y)
        best_sse = float('inf')
        best_split = None
        for split_val in X:
            (X_left, X_right, y_left, y_right) = split_data(X, y, split_val)
            if len(X_left) == 0 or len(X_right) == 0:
                continue
            current_sse = sse(y_left) + sse(y_right)
            if current_sse < best_sse:
                best_sse = current_sse
                best_split = (X_left, X_right, y_left, y_right, split_val)
        if best_split is None:
            return np.mean(y)
        (X_left, X_right, y_left, y_right, split_val) = best_split
        left_tree = build_tree(X_left, y_left, depth + 1)
        right_tree = build_tree(X_right, y_right, depth + 1)
        return (split_val, left_tree, right_tree)

    def predict_tree(tree, x):
        if isinstance(tree, float):
            return tree
        (split_val, left_tree, right_tree) = tree
        if x <= split_val:
            return predict_tree(left_tree, x)
        else:
            return predict_tree(right_tree, x)
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)
    tree = build_tree(X_train, y_train)
    predictions = [predict_tree(tree, x) for x in X_test]
    return predictions

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def compute_grad(x):
        """Compute the gradient of the sigmoid function for a single value."""
        return np.round(x * (1 - x), 4)
    if isinstance(z, (float, int)):
        return compute_grad(z)
    elif isinstance(z, np.ndarray):
        return np.round(z * (1 - z), 4)
    elif isinstance(z, list):
        if not z:
            return []
        return [sigmoid_grad(item) if isinstance(item, list) else compute_grad(item) for item in z]
    else:
        raise ValueError('Unsupported input type. Please provide a float, list, or NumPy ndarray.')

import numpy as np
from collections import deque
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """Density-Based Spatial Clustering (DBSCAN).

    Parameters
    ----------
    data : np.ndarray
        A 2-D array with shape (n_samples, n_features).
    eps : float
        Radius that defines the neighbourhood of a point.
    min_samples : int
        Minimum number of points required to form a dense region (core point).

    Returns
    -------
    list[int]
        Cluster labels for every sample.  Noise points receive the label –1
        while clusters are numbered 1, 2, 3, … in the order they are found.
    """

    def euclidean_distance(point1, point2):
        return np.sqrt(np.sum((point1 - point2) ** 2))

    def get_neighbors(point_index):
        neighbors = []
        for i in range(len(data)):
            if euclidean_distance(data[point_index], data[i]) < eps:
                neighbors.append(i)
        return neighbors

    def expand_cluster(point_index, cluster_id):
        seeds = deque([point_index])
        while seeds:
            current_point = seeds.popleft()
            if labels[current_point] == -1:
                labels[current_point] = cluster_id
            elif labels[current_point] == 0:
                labels[current_point] = cluster_id
                current_neighbors = get_neighbors(current_point)
                if len(current_neighbors) >= min_samples:
                    seeds.extend(current_neighbors)
    n_samples = len(data)
    labels = [0] * n_samples
    cluster_id = 1
    for point_index in range(n_samples):
        if labels[point_index] != 0:
            continue
        neighbors = get_neighbors(point_index)
        if len(neighbors) < min_samples:
            labels[point_index] = -1
        else:
            expand_cluster(point_index, cluster_id)
            cluster_id += 1
    return labels

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

