import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    (n_samples, n_features) = data.shape
    height_limit = math.ceil(math.log2(sample_size))
    c_values = [0] + [2 * math.log(i - 1) + 0.5772156649 - 2 * (i - 1) / i for i in range(1, n_samples + 1)]

    def build_tree(data_subset, depth=0):
        if depth == height_limit or len(data_subset) <= 1:
            return len(data_subset)
        feature = np.random.randint(n_features)
        (min_val, max_val) = (data_subset[:, feature].min(), data_subset[:, feature].max())
        if min_val == max_val:
            return len(data_subset)
        threshold = np.random.uniform(min_val, max_val)
        left_indices = data_subset[:, feature] <= threshold
        right_indices = data_subset[:, feature] > threshold
        left = build_tree(data_subset[left_indices], depth + 1)
        right = build_tree(data_subset[right_indices], depth + 1)
        return (feature, threshold, left, right)

    def path_length(tree, x, depth=0):
        if isinstance(tree, int):
            return depth + c_values[tree]
        (feature, threshold, left, right) = tree
        if x[feature] <= threshold:
            return path_length(left, x, depth + 1)
        else:
            return path_length(right, x, depth + 1)

    def compute_scores(data, trees):
        scores = np.zeros(n_samples)
        for tree in trees:
            for (i, x) in enumerate(data):
                scores[i] += path_length(tree, x)
        scores /= len(trees)
        scores = 2 ** (-scores / (2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples))
        return scores
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            sample = data
        else:
            sample = data[np.random.choice(n_samples, sample_size, replace=False)]
        trees.append(build_tree(sample))
    scores = compute_scores(data, trees)
    threshold = np.percentile(scores, (1 - epsilon) * 100)
    outliers = np.where(scores >= threshold)[0]
    return sorted(outliers.tolist())

