import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    labels = np.unique(y_train)
    num_labels = len(labels)
    num_features = X_train.shape[1]
    weights = defaultdict(float)
    empirical_counts = defaultdict(int)
    for (x, y) in zip(X_train, y_train):
        for (feature_index, feature_value) in enumerate(x):
            empirical_counts[feature_index, feature_value, y] += 1
    empirical_probs = {k: v / len(X_train) for (k, v) in empirical_counts.items()}
    for _ in range(n_iter):
        largest_update = 0
        for ((feature_index, feature_value, y), empirical_prob) in empirical_probs.items():
            expected_count = 0
            for x in X_train:
                if x[feature_index] == feature_value:
                    expected_count += np.exp(sum((weights[feature_index, x[f], y] for f in range(num_features)))) / sum((np.exp(sum((weights[feature_index, x[f], y_prime] for f in range(num_features)))) for y_prime in labels))
            update = math.log(empirical_prob * len(X_train) / expected_count)
            weights[feature_index, feature_value, y] += update
            largest_update = max(largest_update, abs(update))
        if largest_update < epsilon:
            break
    predictions = []
    for x in X_test:
        max_prob = -float('inf')
        predicted_label = None
        for y in labels:
            prob = sum((weights[feature_index, x[feature_index], y] for feature_index in range(num_features)))
            if prob > max_prob:
                max_prob = prob
                predicted_label = y
        predictions.append(predicted_label)
    return predictions

import numpy as np
def euclidean_distance(point1, point2):
    """Calculate the Euclidean distance between two points."""
    return np.sqrt(np.sum((point1 - point2) ** 2))
def pairwise_distance_matrix(data):
    """Compute the full pairwise Euclidean distance matrix."""
    n_samples = data.shape[0]
    distance_matrix = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(i, n_samples):
            distance = euclidean_distance(data[i], data[j])
            distance_matrix[i, j] = distance
            distance_matrix[j, i] = distance
    return distance_matrix
def k_distance(distance_matrix, k):
    """Compute the k-distance for each sample and the index list of k nearest neighbours."""
    n_samples = distance_matrix.shape[0]
    k_distances = np.zeros(n_samples)
    k_nearest_neighbours = []
    for i in range(n_samples):
        sorted_indices = np.argsort(distance_matrix[i])
        k_nearest_neighbours.append(sorted_indices[1:k + 1])
        k_distances[i] = distance_matrix[i, sorted_indices[k]]
    return (k_distances, k_nearest_neighbours)
def reachability_distance(distance_matrix, k_distances, k_nearest_neighbours):
    """Compute the reachability distance for each pair of points."""
    n_samples = distance_matrix.shape[0]
    reachability_distances = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in k_nearest_neighbours[i]:
            reachability_distances[i, j] = max(k_distances[j], distance_matrix[i, j])
    return reachability_distances
def local_reachability_density(reachability_distances, k_nearest_neighbours):
    """Compute the Local Reachability Density for each sample."""
    n_samples = reachability_distances.shape[0]
    lrd = np.zeros(n_samples)
    for i in range(n_samples):
        lrd[i] = len(k_nearest_neighbours[i]) / np.sum(reachability_distances[i, k_nearest_neighbours[i]])
    return lrd
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor for each sample."""
    distance_matrix = pairwise_distance_matrix(data)
    (k_distances, k_nearest_neighbours) = k_distance(distance_matrix, k)
    reachability_distances = reachability_distance(distance_matrix, k_distances, k_nearest_neighbours)
    lrd = local_reachability_density(reachability_distances, k_nearest_neighbours)
    lof_scores = np.zeros(data.shape[0])
    for i in range(data.shape[0]):
        lof_scores[i] = np.sum(lrd[k_nearest_neighbours[i]]) / (k * lrd[i])
    lof_scores = np.round(lof_scores, 4)
    outlier_indices = [i for (i, score) in enumerate(lof_scores) if score > epsilon]
    return (lof_scores.tolist(), sorted(outlier_indices))
