import numpy as np
def isolation_tree_path_length(data: np.ndarray) -> list[int]:
    """Compute the depth of the leaf reached by every sample in a random isolation tree.

    The tree is built following the rules described in the task statement and the
    random seed must be set to 0 so that results are reproducible.

    Args:
        data: A 2-D NumPy array of shape (n_samples, n_features).

    Returns:
        A list of length *n_samples* where the *i-th* element is the number of
        edges from the root to the leaf that contains the *i-th* sample.
    """
    np.random.seed(0)

    def build_tree(indices, depth):
        if len(indices) < 3:
            return (indices, depth)
        f = np.random.randint(data.shape[1])
        down = np.min(data[indices, f])
        up = np.max(data[indices, f])
        v = np.random.uniform(down, up)
        left_indices = indices[data[indices, f] <= v]
        right_indices = indices[data[indices, f] > v]
        left_tree = build_tree(left_indices, depth + 1)
        right_tree = build_tree(right_indices, depth + 1)
        return (left_tree, right_tree)

    def find_depth(tree, sample_index, current_depth):
        if isinstance(tree, int):
            return current_depth if tree == sample_index else None
        if isinstance(tree, tuple):
            (left_tree, right_tree) = tree
            if sample_index in left_tree[0]:
                return find_depth(left_tree, sample_index, current_depth)
            else:
                return find_depth(right_tree, sample_index, current_depth)
        return find_depth(tree[0], sample_index, current_depth + 1)
    all_indices = np.arange(data.shape[0])
    tree = build_tree(all_indices, 0)
    depths = [find_depth(tree, i, 0) for i in range(data.shape[0])]
    return depths
