import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement the k-Nearest Neighbors classifier."""

    def euclidean_distance(x1, x2):
        return np.sqrt(np.sum((x1 - x2) ** 2, axis=1))

    def manhattan_distance(x1, x2):
        return np.sum(np.abs(x1 - x2), axis=1)

    def cosine_distance(x1, x2):
        dot_product = np.sum(x1 * x2, axis=1)
        norm_x1 = np.sqrt(np.sum(x1 ** 2, axis=1))
        norm_x2 = np.sqrt(np.sum(x2 ** 2, axis=1))
        return 1 - dot_product / (norm_x1 * norm_x2)
    if metric == 'manhattan':
        distance_metric = manhattan_distance
    elif metric == 'cosine':
        distance_metric = cosine_distance
    else:
        distance_metric = euclidean_distance
    predictions = np.zeros(X_test.shape[0])
    for (i, test_sample) in enumerate(X_test):
        distances = distance_metric(X_train, np.tile(test_sample, (X_train.shape[0], 1)))
        k_indices = np.argsort(distances)[:k]
        k_nearest_labels = y_train[k_indices]
        (values, counts) = np.unique(k_nearest_labels, return_counts=True)
        predictions[i] = values[np.argmax(counts)]
        if np.max(counts) == 1 or np.sum(counts == np.max(counts)) > 1:
            predictions[i] = np.min(values)
    return predictions

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:

    def decision_stump(X, y, weights):
        min_error = float('inf')
        best_stump = {}
        for feature_index in range(len(X[0])):
            for threshold in np.unique(X[:, feature_index]):
                for polarity in [-1, 1]:
                    predictions = np.where(X[:, feature_index] < threshold if polarity == 1 else X[:, feature_index] >= threshold, 1, -1)
                    error = np.sum(weights * (predictions != y))
                    if error < min_error:
                        min_error = error
                        best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity, 'error': error}
                    elif error == min_error:
                        if feature_index < best_stump['feature_index']:
                            best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity, 'error': error}
                        elif feature_index == best_stump['feature_index'] and threshold < best_stump['threshold']:
                            best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity, 'error': error}
                        elif feature_index == best_stump['feature_index'] and threshold == best_stump['threshold'] and (polarity == 1):
                            best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity, 'error': error}
        return best_stump

    def stump_predict(stump, X):
        return np.where(X[:, stump['feature_index']] < stump['threshold'] if stump['polarity'] == 1 else X[:, stump['feature_index']] >= stump['threshold'], 1, -1)
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)
    y_train[y_train == 0] = -1
    weights = np.full(len(y_train), 1 / len(y_train))
    stumps = []
    alpha_list = []
    for _ in range(n_estimators):
        stump = decision_stump(X_train, y_train, weights)
        predictions = stump_predict(stump, X_train)
        error = np.sum(weights * (predictions != y_train))
        if error == 0:
            error += 1e-10
        alpha = 0.5 * np.log((1 - error) / error)
        weights *= np.exp(-alpha * y_train * predictions)
        weights /= np.sum(weights)
        stumps.append(stump)
        alpha_list.append(alpha)
        if error == 0:
            break
    test_predictions = np.zeros(len(X_test))
    for (stump, alpha) in zip(stumps, alpha_list):
        test_predictions += alpha * stump_predict(stump, X_test)
    test_predictions = np.sign(test_predictions)
    test_predictions[test_predictions == -1] = 0
    return test_predictions.tolist()
