import numpy as np
def gini_impurity(y):
    """Calculate the Gini impurity of a dataset."""
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def best_split(X, y, max_depth):
    """Find the best split for a node."""
    best_gini_gain = -1
    best_feature = None
    best_threshold = None
    if len(np.unique(y)) == 1 or (max_depth is not None and max_depth == 0):
        return (None, None, None)
    n_features = X.shape[1]
    for feature in range(n_features):
        feature_values = np.sort(np.unique(X[:, feature]))
        thresholds = (feature_values[:-1] + feature_values[1:]) / 2
        for threshold in thresholds:
            left_indices = X[:, feature] <= threshold
            right_indices = X[:, feature] > threshold
            if not left_indices.any() or not right_indices.any():
                continue
            left_gini = gini_impurity(y[left_indices])
            right_gini = gini_impurity(y[right_indices])
            gini_gain = gini_impurity(y) - (left_gini * sum(left_indices) + right_gini * sum(right_indices)) / len(y)
            if gini_gain > best_gini_gain:
                best_gini_gain = gini_gain
                best_feature = feature
                best_threshold = threshold
    return (best_feature, best_threshold, best_gini_gain)
def build_tree(X, y, max_depth=None):
    """Recursively build the decision tree."""
    if len(np.unique(y)) == 1 or (max_depth is not None and max_depth == 0):
        return {'leaf': True, 'label': np.argmax(np.bincount(y))}
    (feature, threshold, gini_gain) = best_split(X, y, max_depth)
    if gini_gain <= 0:
        return {'leaf': True, 'label': np.argmax(np.bincount(y))}
    left_indices = X[:, feature] <= threshold
    right_indices = X[:, feature] > threshold
    left_subtree = build_tree(X[left_indices], y[left_indices], None if max_depth is None else max_depth - 1)
    right_subtree = build_tree(X[right_indices], y[right_indices], None if max_depth is None else max_depth - 1)
    return {'leaf': False, 'feature': feature, 'threshold': threshold, 'left': left_subtree, 'right': right_subtree}
def predict_single(sample, tree):
    """Predict the class label of a single sample."""
    if tree['leaf']:
        return tree['label']
    if sample[tree['feature']] <= tree['threshold']:
        return predict_single(sample, tree['left'])
    else:
        return predict_single(sample, tree['right'])
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    tree = build_tree(X_train, y_train, max_depth=max_depth)
    predictions = [predict_single(sample, tree) for sample in X_test]
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function."""

    def validate(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        if len(x) == 0 or len(y) == 0 or len(x) != len(y):
            return False
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except ValueError:
            return False
        return (x, y)

    def euclidean(x, y):
        return round(np.sqrt(np.sum((x - y) ** 2)), 4)

    def manhattan(x, y):
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        return round(1 - np.dot(x, y) / (norm_x * norm_y), 4)

    def d(x, y):
        validated = validate(x, y)
        if not validated:
            return -1
        (x, y) = validated
        if name == 'euclidean':
            return euclidean(x, y)
        elif name == 'manhattan':
            return manhattan(x, y)
        elif name == 'chebyshev':
            return chebyshev(x, y)
        elif name == 'cosine':
            return cosine(x, y)
        else:
            return -1
    return d

def calculate_score(hand: list[int]) -> int:
    """Calculate the score of a hand in Blackjack."""
    score = sum(hand)
    aces = hand.count(1)
    while aces > 0 and score + 10 <= 21:
        score += 10
        aces -= 1
    return score if score <= 21 else 0
def is_natural_blackjack(hand: list[int]) -> bool:
    """Check if the hand is a natural blackjack."""
    return len(hand) == 2 and 1 in hand and (10 in hand)
def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score == 0:
        return -1
    elif dealer_score == 0:
        return 1
    elif is_natural_blackjack(player):
        return 1.5
    elif player_score > dealer_score:
        return 1
    elif player_score < dealer_score:
        return -1
    else:
        return 0

import numpy as np
def sigmoid(z):
    """Compute the element-wise sigmoid (logistic) function.

    The function must work for scalar numbers, Python lists and NumPy arrays and
    must be numerically stable for very large positive or negative inputs.

    Args:
        z: A scalar (int/float) or array-like object (list or np.ndarray)
           containing numeric values.

    Returns:
        float | list: The sigmoid of the input rounded to 4 decimal places.
        If `z` is a scalar the return value is a float, otherwise it is a Python
        list with the same shape as the input.
    """
    z = np.array(z)
    with np.errstate(over='ignore', under='ignore'):
        if z.ndim == 0:
            if z >= 0:
                return round(1 / (1 + np.exp(-z)), 4)
            else:
                return round(np.exp(z) / (1 + np.exp(z)), 4)
        else:
            result = np.where(z >= 0, 1 / (1 + np.exp(-z)), np.exp(z) / (1 + np.exp(z)))
            return [round(num, 4) for num in result.tolist()]

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value)
    elif isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature != tree_b.feature or not np.isclose(tree_a.threshold, tree_b.threshold):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    else:
        return False

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """

    def hz_to_mel(f):
        return 2595 * np.log10(1 + f / 700)

    def mel_to_hz(mel):
        return 700 * (10 ** (mel / 2595) - 1)
    if max_freq is None:
        max_freq = fs / 2
    mel_points = np.linspace(hz_to_mel(min_freq), hz_to_mel(max_freq), n_filters + 2)
    hz_points = mel_to_hz(mel_points)
    bins = np.fft.rfftfreq(N) * fs
    filterbank = np.zeros((n_filters, N // 2 + 1))
    for i in range(n_filters):
        (left_hz, center_hz, right_hz) = (hz_points[i], hz_points[i + 1], hz_points[i + 2])
        for k in range(N // 2 + 1):
            freq = bins[k]
            if freq < left_hz or freq > right_hz:
                filterbank[i, k] = 0
            elif freq <= center_hz:
                filterbank[i, k] = (freq - left_hz) / (center_hz - left_hz)
            else:
                filterbank[i, k] = (right_hz - freq) / (right_hz - center_hz)
        if normalize:
            filterbank[i] *= 2 / (right_hz - left_hz)
    return [list(np.round(row, 4)) for row in filterbank]
