import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    def mse(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    def split_mse(X, y, index, threshold):
        left_indices = X[:, index] < threshold
        right_indices = X[:, index] >= threshold
        left_mse = mse(y[left_indices], np.mean(y[left_indices]))
        right_mse = mse(y[right_indices], np.mean(y[right_indices]))
        return left_mse + right_mse

    def best_split(X, y):
        (best_index, best_threshold, best_mse) = (None, None, float('inf'))
        for index in range(X.shape[1]):
            for threshold in np.unique(X[:, index]):
                mse = split_mse(X, y, index, threshold)
                if mse < best_mse:
                    (best_mse, best_index, best_threshold) = (mse, index, threshold)
        return (best_index, best_threshold)

    def build_tree(X, y, depth=0):
        if depth == max_depth or len(np.unique(y)) == 1:
            return np.mean(y)
        (index, threshold) = best_split(X, y)
        left_indices = X[:, index] < threshold
        right_indices = X[:, index] >= threshold
        left_tree = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_tree = build_tree(X[right_indices], y[right_indices], depth + 1)
        return (index, threshold, left_tree, right_tree)

    def predict_tree(tree, x):
        if type(tree) != tuple:
            return tree
        (index, threshold, left_tree, right_tree) = tree
        if x[index] < threshold:
            return predict_tree(left_tree, x)
        else:
            return predict_tree(right_tree, x)
    (n_samples, n_features) = X_train.shape
    predictions = np.full(n_samples, np.mean(y_train))
    final_predictions = []
    for _ in range(n_estimators):
        residuals = y_train - predictions
        tree = build_tree(X_train, residuals)
        predictions += learning_rate * np.array([predict_tree(tree, x) for x in X_train])
    for x in X_test:
        prediction = np.mean(y_train)
        for _ in range(n_estimators):
            tree = build_tree(X_train, y_train - prediction)
            prediction += learning_rate * predict_tree(tree, x)
        final_predictions.append(round(prediction, 4))
    return final_predictions

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    is_list = isinstance(x, list)
    x = np.array(x) if is_list or isinstance(x, np.ndarray) else np.array([x])
    sig = 1 / (1 + np.exp(-x))
    d_sig = sig * (1 - sig)
    d2_sig = d_sig * (1 - 2 * sig)
    if order == 0:
        result = sig
    elif order == 1:
        result = d_sig
    else:
        result = d2_sig
    result = np.round(result, 4)
    if is_list:
        return result.tolist()
    elif isinstance(x, np.ndarray) and x.size == 1:
        return float(result)
    else:
        return result.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X = np.array(X)
    if Y is not None:
        Y = np.array(Y)
    else:
        Y = X
    if X.shape[1] != Y.shape[1]:
        return -1
    C = X.shape[1]
    if isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
        sigma = np.array([sigma] * C)
    elif isinstance(sigma, (list, np.ndarray)):
        sigma = np.array(sigma)
        if len(sigma) != C or np.any(sigma <= 0):
            return -1
    elif sigma is None:
        sigma = np.sqrt(C / 2)
        sigma = np.array([sigma] * C)
    else:
        return -1
    kernel_matrix = np.zeros((X.shape[0], Y.shape[0]))
    for (i, x) in enumerate(X):
        for (j, y) in enumerate(Y):
            diff = (x - y) / sigma
            kernel_matrix[i, j] = np.exp(-0.5 * np.sum(diff ** 2))
    kernel_matrix = np.round(kernel_matrix, 4).tolist()
    return kernel_matrix

import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """

    def build_tree(X, y, depth):
        if depth == max_depth or len(X) < 2:
            return np.mean(y)
        best_split = None
        best_sse = float('inf')
        for split in set(X):
            left_y = [y[i] for i in range(len(X)) if X[i] < split]
            right_y = [y[i] for i in range(len(X)) if X[i] >= split]
            if not left_y or not right_y:
                continue
            sse = np.sum((left_y - np.mean(left_y)) ** 2) + np.sum((right_y - np.mean(right_y)) ** 2)
            if sse < best_sse:
                best_sse = sse
                best_split = split
        if best_split is None:
            return np.mean(y)
        left_indices = [i for i in range(len(X)) if X[i] < best_split]
        right_indices = [i for i in range(len(X)) if X[i] >= best_split]
        left_tree = build_tree([X[i] for i in left_indices], [y[i] for i in left_indices], depth + 1)
        right_tree = build_tree([X[i] for i in right_indices], [y[i] for i in right_indices], depth + 1)
        return (best_split, left_tree, right_tree)

    def predict(point, tree):
        if isinstance(tree, float):
            return tree
        (split, left_tree, right_tree) = tree
        if point < split:
            return predict(point, left_tree)
        else:
            return predict(point, right_tree)
    if not X_train:
        return []
    tree = build_tree(X_train, y_train, 0)
    return [predict(x, tree) for x in X_test]

