import numpy as np
def sigmoid(z):
    """Compute the numerically stable, element-wise sigmoid activation.

    The function must work for a scalar, list/tuple, or NumPy ndarray input.

    Args:
        z: int, float, list, tuple, or numpy.ndarray – input values.

    Returns:
        float if *z* is a scalar, otherwise a Python list obtained with
        ``numpy.ndarray.tolist()``, where every element is the sigmoid of the
        corresponding input value and is rounded to four decimal places.
    """
    if isinstance(z, (list, tuple)):
        z = np.array(z)
    elif not isinstance(z, np.ndarray):
        z = np.array(z, ndmin=0)
    with np.errstate(over='ignore'):
        sig = np.where(z >= 0, 1 / (1 + np.exp(-z)), np.exp(z) / (1 + np.exp(z)))
    sig_rounded = np.around(sig, decimals=4)
    if z.ndim == 0:
        return float(sig_rounded)
    else:
        return sig_rounded.tolist()

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def compute_grad(z):
        if isinstance(z, (list, np.ndarray)):
            return [compute_grad(item) for item in z]
        else:
            return np.round(z * (1 - z), 4)
    if isinstance(z, (int, float)):
        return np.round(z * (1 - z), 4)
    return compute_grad(z)

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import numpy as np
import math
def build_tree(data, current_depth, height_limit):
    n = len(data)
    if n <= 1 or current_depth >= height_limit:
        return {'leaf': True, 'count': n}
    feature = np.random.randint(data.shape[1])
    (min_val, max_val) = (np.min(data[:, feature]), np.max(data[:, feature]))
    if min_val == max_val:
        return {'leaf': True, 'count': n}
    threshold = np.random.uniform(min_val, max_val)
    left_indices = data[:, feature] <= threshold
    right_indices = data[:, feature] > threshold
    left_data = data[left_indices]
    right_data = data[right_indices]
    return {'leaf': False, 'feature': feature, 'threshold': threshold, 'left': build_tree(left_data, current_depth + 1, height_limit), 'right': build_tree(right_data, current_depth + 1, height_limit)}
def path_length(tree, x, current_depth=0):
    if tree['leaf']:
        n = tree['count']
        return current_depth + c(n)
    feature = tree['feature']
    threshold = tree['threshold']
    if x[feature] <= threshold:
        return path_length(tree['left'], x, current_depth + 1)
    else:
        return path_length(tree['right'], x, current_depth + 1)
def c(n):
    if n == 1:
        return 0
    else:
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
def average_path_length(data, tree):
    return np.array([path_length(tree, x) for x in data])
def anomaly_score(path_length, n_samples):
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    return 2 ** (-path_length / phi)
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    (n_samples, n_features) = data.shape
    height_limit = math.ceil(math.log2(sample_size))
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            sample = data
        else:
            sample_indices = np.random.choice(n_samples, sample_size, replace=False)
            sample = data[sample_indices]
        tree = build_tree(sample, 0, height_limit)
        trees.append(tree)
    avg_path_lengths = np.zeros(n_samples)
    for tree in trees:
        avg_path_lengths += average_path_length(data, tree)
    avg_path_lengths /= n_trees
    scores = anomaly_score(avg_path_lengths, n_samples)
    threshold = np.percentile(scores, 100 * (1 - epsilon))
    outliers = np.where(scores > threshold)[0].tolist()
    return sorted(outliers)

