import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    feature_map = defaultdict(lambda : len(feature_map))
    labels = np.unique(y_train)
    for i in range(X_train.shape[1]):
        for value in np.unique(X_train[:, i]):
            for label in labels:
                feature_map[i, value, label] = len(feature_map)
    num_features = len(feature_map)
    weights = np.zeros(num_features)
    empirical_expectations = np.zeros(num_features)
    for (x, y) in zip(X_train, y_train):
        for (i, value) in enumerate(x):
            empirical_expectations[feature_map[i, value, y]] += 1
    empirical_expectations /= len(X_train)
    for _ in range(n_iter):
        model_expectations = np.zeros(num_features)
        for (x, y) in zip(X_train, y_train):
            scores = np.zeros(len(labels))
            for (i, value) in enumerate(x):
                for (j, label) in enumerate(labels):
                    scores[j] += weights[feature_map[i, value, label]]
            prob = np.exp(scores - logsumexp(scores))
            for (i, value) in enumerate(x):
                model_expectations[feature_map[i, value, y]] += prob[y]
        model_expectations /= len(X_train)
        delta = empirical_expectations - model_expectations
        max_update = np.max(np.abs(delta))
        if max_update < epsilon:
            break
        for i in range(num_features):
            if model_expectations[i] > 0:
                weights[i] += math.log((empirical_expectations[i] + 1e-10) / (model_expectations[i] + 1e-10))
    predictions = []
    for x in X_test:
        scores = np.zeros(len(labels))
        for (i, value) in enumerate(x):
            for (j, label) in enumerate(labels):
                scores[j] += weights[feature_map[i, value, label]]
        predictions.append(labels[np.argmax(scores)])
    return predictions
def logsumexp(scores):
    max_score = np.max(scores)
    if max_score == -np.inf:
        return -np.inf
    return max_score + np.log(np.sum(np.exp(scores - max_score)))

import numpy as np
def euclidean_distance(point1, point2):
    """Compute the Euclidean distance between two points."""
    return np.sqrt(np.sum((point1 - point2) ** 2))
def pairwise_distance_matrix(data):
    """Compute the full pair-wise Euclidean distance matrix."""
    n_samples = len(data)
    distance_matrix = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(i, n_samples):
            distance = euclidean_distance(data[i], data[j])
            distance_matrix[i, j] = distance
            distance_matrix[j, i] = distance
    return distance_matrix
def k_distance(distance_matrix, k):
    """Get the k-distance for each sample and the index list of its k nearest neighbors."""
    n_samples = len(distance_matrix)
    k_distances = np.zeros(n_samples)
    k_nearest_neighbors = [None] * n_samples
    for i in range(n_samples):
        sorted_indices = np.argsort(distance_matrix[i])
        k_distances[i] = distance_matrix[i, sorted_indices[k]]
        k_nearest_neighbors[i] = sorted_indices[1:k + 1]
    return (k_distances, k_nearest_neighbors)
def reachability_distance(distance_matrix, k_distances, point_index, neighbor_index):
    """Compute the reachability distance between two points."""
    return max(k_distances[neighbor_index], distance_matrix[point_index, neighbor_index])
def local_reachability_density(distance_matrix, k_distances, k_nearest_neighbors, point_index):
    """Compute the Local Reachability Density (LRD) for a point."""
    reachability_distances_sum = 0
    for neighbor_index in k_nearest_neighbors[point_index]:
        reachability_distances_sum += reachability_distance(distance_matrix, k_distances, point_index, neighbor_index)
    return len(k_nearest_neighbors[point_index]) / reachability_distances_sum
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor (LOF) for each sample and return the scores and outlier indices."""
    distance_matrix = pairwise_distance_matrix(data)
    (k_distances, k_nearest_neighbors) = k_distance(distance_matrix, k)
    lof_scores = []
    outlier_indices = []
    for i in range(len(data)):
        lrd_i = local_reachability_density(distance_matrix, k_distances, k_nearest_neighbors, i)
        lrd_sum = sum((local_reachability_density(distance_matrix, k_distances, k_nearest_neighbors, q) for q in k_nearest_neighbors[i]))
        lof_score = lrd_sum / k / lrd_i
        lof_scores.append(round(lof_score, 4))
        if lof_score > epsilon:
            outlier_indices.append(i)
    return (lof_scores, sorted(outlier_indices))
